mapper.select:
select   e.item_id,  e.commodity_id, e.evaluate ,e.create_time,e.evaluate_id
, e.user_id,u.user_name,u.user_image
from item_commodity_evaluate e
left join user_info u on u.uid=e.user_id
where commodity_id=#{commodityId}
LIMIT #{page}, #{limit}

select user_image,user_name,uid
from user_info u join item_commodity_evaluate e on u.uid = e.user_id
where u.uid in (
    select user_id
    from item_commodity_evaluate
    where item_id = #{itemId}
)
LIMIT #{page}, #{limit}
