mapper.select:
select o.order_id, o.order_status, o.commodity_id, o.transaction_amount, u.user_image, u.item_name, i.avatar_imag from item_order_info o JOIN item_info u JOIN item_commodity_info i where o.uid = #{getOrderDataVO.userId}
and o.order_status = #{getOrderDataVO.orderStatus}
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.item_id = u.item_id ORDER BY o.update_time ASC;
