mapper.update:
UPDATE shop_info i
     left join shop_commodity_info c
ON i.shop_id = c.shop_id
    SET i.upload_commodity_balance = i.upload_commodity_balance - 1
WHERE c.commodity_id = #{commodityId}
  and i.upload_commodity_balance>0;

UPDATE shop_info
shop_name = #{shopName}, localhost_latitude = #{localhostLatitude}, localhost_longitude = #{localhostLongitude}, tag_list = #{tagList}, phone = #{phone}, address = #{address}, shop_introduce = #{shopIntroduce}, shop_imag_list = #{shopImagList}, user_image = #{userImage}, category_id = #{categoryId}, piratio = #{piratio}, start_time = #{startTime}, end_time = #{endTime},
qrcode = #{qrcode}
WHERE shop_id = #{shopId}

mapper.select:
SELECT s.shop_id,s.shop_name,s.score,i.origin_path as userImage, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id=#{shopId}
) as follows
from shop_info s
left join
     image i
on
    i.image_id = s.user_image
WHERE shop_id=#{shopId}

SELECT s.shop_id,s.status,s.shop_name,s.score,i.origin_path as userImage, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id= s.shop_id
) as follows
from shop_info s
left join image i on i.image_id = s.user_image
WHERE uid=#{uid}
and status = 0 or status = 1
LIMIT #{page},#{limit}

select s.shop_id,s.shop_name,s.score,s.localhost_latitude,s.localhost_longitude,i.origin_path as userImage, s.shop_introduce, s.tag_list,s.address,s.membership from shop_info s left join image i ON i.image_id = s.user_image left join shop_region sr on sr.shop_id = s.shop_id WHERE category_id in (select category_id from shop_category where category_pid = #{categoryId}) and status=0 and sr.del_flag = 0 and sr.region_id = #{regionId}
and s.shop_name like concat('%',#{shopName},'%')
LIMIT ${page},${limit}

select si.shop_id,si.shop_name,si.tag_list,si.address,si.score,si.shop_introduce,si.user_image,si.localhost_latitude,si.localhost_longitude,si.membership from shop_info si left join shop_region sr on sr.shop_id = si.shop_id where si.status = 0 and sr.del_flag = 0 and sr.region_id = #{regionId}
and si.shop_name like concat('%',#{shopName},'%')
and si.shop_id in( select shop_id from shop_commodity_live_info where adult >= #{adult} AND children >= #{children} AND del_flag = 0 AND status = 0 ) limit #{page},#{limit}

select count(*) from shop_info si left join shop_region sr on sr.shop_id = si.shop_id where si.status = 0 and sr.del_flag = 0 and sr.region_id = #{regionId}
and shop_name like concat('%',#{shopName},'%')
and si.shop_id in( select shop_id from shop_commodity_live_info where adult >= #{adult} AND children >= #{children} AND del_flag = 0 AND status = 0 )

select s.shop_id, s.shop_name, s.score, s.localhost_latitude, s.localhost_longitude, i.origin_path as userImage, s.shop_introduce, s.tag_list, s.address, s.membership from shop_info s left join image i ON i.image_id = s.user_image left join shop_region sr on sr.shop_id = s.shop_id where s.category_id = #{categoryId} and s.status = 0 and sr.del_flag = 0 and sr.region_id = #{regionId}
and s.shop_name like concat('%',#{shopName},'%')
LIMIT ${page},${limit}
