mapper.update:
UPDATE shop_info i
    INNER JOIN shop_commodity_info c
ON i.shop_id = c.shop_id
    SET i.upload_commodity_balance = i.upload_commodity_balance - 1
WHERE c.commodity_id = #{commodityId}
  and i.upload_commodity_balance>0;

mapper.select:
SELECT shop_id,shop_name,score,user_image, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id=#{shopId}
) as follows
from shop_info
WHERE shop_id=#{shopId}

SELECT shop_id,shop_name,score,user_image, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id=shop_info.shop_id
) as follows
from shop_info
WHERE uid=#{uid}
and status = 0
    LIMIT #{page},#{limit}

select shop_id,shop_name,tag_list,address,score,shop_introduce,user_image,localhost_latitude,localhost_longitude,membership from shop_info where status = 0
and shop_name like concat('%',#{shopName},'%')
and shop_id in( select shop_id from shop_commodity_live_info where adult >= #{adult} AND children >= #{children} AND del_flag = 0 AND status = 0 ) limit #{page},#{limit}
