mapper.update:
UPDATE shop_info i
    INNER JOIN shop_commodity_info c
ON i.shop_id = c.shop_id
    SET i.upload_commodity_balance = i.upload_commodity_balance - 1
WHERE c.commodity_id = #{commodityId}
  and i.upload_commodity_balance>0;

mapper.select:
SELECT s.shop_id,s.shop_name,s.score,i.origin_path as userImage, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id=#{shopId}
) as follows
from shop_info s
JOIN
     image i
on
    i.image_id = s.user_image
WHERE shop_id=#{shopId}

SELECT s.shop_id,s.shop_name,s.score,i.origin_path as userImage, (
    SELECT COUNT(*) FROM shop_follow_focus
    where `status`=0 and shop_id= s.shop_id
) as follows
from shop_info s
join image i on i.image_id = s.user_image
WHERE uid=#{uid}
and status = 0
    LIMIT #{page},#{limit}

select count(*)
from shop_info
WHERE status=0
and
area_divide = #{areaDivide}


and category_id = #{categoryId}

select s.shop_id, s.shop_name, s.score, s.localhost_latitude, s.localhost_longitude, i.origin_path as userImage, s.shop_introduce, s.tag_list, s.address, s.membership from shop_info s JOIN image i ON i.image_id = s.user_image
s.area_divide = #{areaDivide}
and s.category_id = #{categoryId}
order By score
order By score desc
LIMIT ${page},${limit}

select shop_id,shop_name,tag_list,address,score,shop_introduce,user_image,localhost_latitude,localhost_longitude,membership
from shop_info
where
status = 0 and area_divide = #{areaDivide}

    and shop_name like concat('%',#{shopName},'%')

and shop_id in(
    select shop_id
    from shop_commodity_live_info
    where  adult >= #{adult}
    AND children >= #{children}
    AND del_flag = 0
    AND status = 0
)
limit #{page},#{limit}

select count(*)
from shop_info
where status = 0 and area_divide = #{areaDivide}

    and shop_name like concat('%',#{shopName},'%')

and shop_id in(
    select shop_id
    from shop_commodity_live_info
    where  adult >= #{adult}
    AND children >= #{children}
    AND del_flag = 0
    AND status = 0
)
