mapper.select:
SELECT ui.uid,ui.user_name,ui.user_image,  sci.commodity_id,sci.commodity_name,sci.commodity_img_list,  soi.order_id,soi.transaction_amount
FROM user_info as ui
         INNER JOIN shop_info as si on ui.uid=si.uid
         INNER JOIN shop_commodity_info as sci on si.shop_id=sci.shop_id
         INNER JOIN shop_order_info as soi on sci.shop_id=soi.shop_id
WHERE sci.`status`=1 and soi.del_flag=0
  and si.uid=#{uid}
  and order_status=#{status}
    LIMIT #{page},#{limit}

select o.order_id, o.order_status, o.commodity_id, o.transaction_amount, g.origin_path as user_image, u.shop_name,e.origin_path as avatar_imag, i.commodity_detail from shop_order_info o JOIN shop_info u JOIN shop_commodity_info i join image g on g.image_id=u.user_image join image e on e.image_id=i.avatar_imag where o.shop_id = #{getOrderDataVO.shopId}
and o.order_status != 3
and o.order_status = 0
and o.order_status != 3 and o.order_status != 0
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.shop_id = u.shop_id ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize}

select o.order_id,o.order_status,o.commodity_id,o.transaction_amount, e.origin_path as avatar_imag,l.room_type_name, s.address,g.origin_path as user_image,s.shop_name,s.shop_introduce ,r.start_time,r.end_time FROM shop_order_info as o join shop_commodity_live_info as l ON o.commodity_id=l.room_id join shop_info as s ON s.shop_id = o.shop_id JOIN shop_hotel_record as r on o.order_id=r.order_id join image g on g.image_id=s.user_image join image e on e.image_id=l.avatar_imag WHERE o.uid=#{getOrderDataVO.userId}
and o.order_status = #{getOrderDataVO.orderStatus}
ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize};

select count(*) FROM shop_order_info as o join shop_commodity_live_info as l ON o.commodity_id=l.room_id join shop_info as s ON s.shop_id = o.shop_id JOIN shop_hotel_record as r on o.order_id=r.order_id WHERE o.uid=#{getOrderDataVO.userId}
and o.order_status = #{getOrderDataVO.orderStatus}
ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize};

select o.order_id,o.order_status,o.commodity_id,o.transaction_amount, g.origin_path as avatar_imag,l.room_type_name, s.address,e.origin_path as user_image,s.shop_name,s.shop_introduce ,r.start_time,r.end_time FROM shop_order_info as o join shop_commodity_live_info as l ON o.commodity_id=l.room_id join shop_info as s ON s.shop_id = o.shop_id JOIN shop_hotel_record as r on o.order_id=r.order_id join image g on g.image_id=l.avatar_imag join image e on e.image_id=s.user_image WHERE s.uid=#{getOrderDataVO.userId}
and o.order_status = #{getOrderDataVO.orderStatus}
ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize};

select count(*) FROM shop_order_info as o join shop_commodity_live_info as l ON o.commodity_id=l.room_id join shop_info as s ON s.shop_id = o.shop_id JOIN shop_hotel_record as r on o.order_id=r.order_id WHERE s.uid=#{getOrderDataVO.userId}
and o.order_status = #{getOrderDataVO.orderStatus}
ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize};
