mapper.select:
SELECT ui.uid,ui.user_name,ui.user_image,  sci.commodity_id,sci.commodity_name,sci.commodity_img_list,  soi.order_id,soi.transaction_amount
FROM user_info as ui
         INNER JOIN shop_info as si on ui.uid=si.uid
         INNER JOIN shop_commodity_info as sci on si.shop_id=sci.shop_id
         INNER JOIN shop_order_info as soi on sci.shop_id=soi.shop_id
WHERE sci.`status`=1 and soi.del_flag=0
  and si.uid=#{uid}
  and order_status=#{status}
    LIMIT #{page},#{limit}

select o.order_id, o.order_status, o.commodity_id, o.transaction_amount, u.user_image, u.shop_name, i.avatar_imag, i.commodity_detail from shop_order_info o JOIN shop_info u JOIN shop_commodity_info i where o.uid = #{getOrderDataVO.userId}
and o.order_status != 3
and o.order_status = #{getOrderDataVO.orderStatus}
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.shop_id = u.shop_id ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize};

select o.order_id, o.order_status, o.commodity_id, o.transaction_amount, u.user_image, u.shop_name, i.avatar_imag, i.commodity_detail from shop_order_info o JOIN shop_info u JOIN shop_commodity_info i where o.shop_id = #{getOrderDataVO.shopId}
and o.order_status != 3
and o.order_status = #{getOrderDataVO.orderStatus}
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.shop_id = u.shop_id ORDER BY o.update_time ASC LIMIT #{getOrderDataVO.currentPage},#{getOrderDataVO.pageSize}

select COUNT(1) from shop_order_info o JOIN shop_info u JOIN shop_commodity_info i where o.shop_id = #{getOrderDataVO.shopId}
and o.order_status != 3
and o.order_status = #{getOrderDataVO.orderStatus}
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.shop_id = u.shop_id

select count(1) from shop_order_info o JOIN shop_info u JOIN shop_commodity_info i where o.uid = #{getOrderDataVO.userId}
and o.order_status != 3
and o.order_status = #{getOrderDataVO.orderStatus}
AND o.del_flag = 0 AND o.commodity_id = i.commodity_id AND o.shop_id = u.shop_id
